export type ElementTags = "HtmlElement" | "ComponentElement" | "Text";

export type Element = {
  type: ElementTags;
};

export type Node = Element | string | number | boolean | undefined | null;

export type FC<P = {}> = (props: P) => Element;

export type Ref<T> = {
  current: T;
};

export function h<P extends {} = {}>(
  tag: HtmlTags | FC,
  props?: P,
  ...children: Node[]
): Element;

export function h<P extends {} = {}>(
  tag: HtmlTags | FC<P>,
  props: P,
  ...children: Node[]
): Element;

export function useRef<T>(v: T): Ref<T>;

export function useEffect(effect: () => void, deps: any[]): void;
